mapper.select:
SELECT VERSE,CONTENTS FROM TB_BIBLE 
	WHERE TYPE = #{type} 
		AND VERSION =#{version} 
		AND CHATER =#{chapter}
		AND GOSPEL =#{gospel}
		AND VERSE =#{verse}

		SELECT VERSION,GOSPEL,TYPE,CHAPTER,COUNT(*) AS VERSE,MIN(SEQ) AS SEQ
		FROM TB_BIBLE
		WHERE VERSION= #{version}
            AND TYPE IN ('BC','AD')
		GROUP BY VERSION,GOSPEL,TYPE,CHAPTER
        ORDER BY SEQ;
